import React, { useState, useEffect } from "react";
import { jsPDF } from "jspdf";
import axios from "axios";
import "./dashboard.css";

export const Dashboard = ({ user }) => {
  const [view, setView] = useState("home");
  const [employees, setEmployees] = useState([]);
  const [attendance, setAttendance] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const empRes = await axios.get("http://localhost:5000/api/employees");
        const attRes = await axios.get("http://localhost:5000/api/attendance");
        setEmployees(empRes.data);
        setAttendance(attRes.data);
      } catch (err) {
        console.error("Error fetching data:", err);
      }
    };
    fetchData();
  }, []);

  const calculateNetSalary = (emp) => emp.basicSalary + emp.allowances - emp.deductions;

  const generateSalarySlip = (emp) => {
    const doc = new jsPDF();
    doc.setFontSize(18);
    doc.text("Salary Slip", 80, 20);

    doc.setFontSize(12);
    doc.text(`Name: ${emp.name}`, 20, 40);
    doc.text(`Email: ${emp.email}`, 20, 50);
    doc.text(`Designation: ${emp.designation}`, 20, 60);
    doc.text(`Basic Salary: â‚¹${emp.basicSalary}`, 20, 80);
    doc.text(`Allowances: â‚¹${emp.allowances}`, 20, 90);
    doc.text(`Deductions: â‚¹${emp.deductions}`, 20, 100);
    doc.text(`Net Salary: â‚¹${calculateNetSalary(emp)}`, 20, 120);

    doc.save(`${emp.name}_SalarySlip.pdf`);
  };

  const attendanceColor = (status) => {
    switch (status) {
      case "Present": return "text-success";
      case "Absent": return "text-danger";
      case "Leave": return "text-warning";
      default: return "";
    }
  };

  return (
    <div className="dashboard-container container-fluid">
      <div className="row mt-4">
        <div className="col-md-3">
          <div className="list-group">
            <button className={`list-group-item list-group-item-action ${view==="home" && "active"}`} onClick={() => setView("home")}>Home</button>
            <button className={`list-group-item list-group-item-action ${view==="employees" && "active"}`} onClick={() => setView("employees")}>Employees</button>
            <button className={`list-group-item list-group-item-action ${view==="payroll" && "active"}`} onClick={() => setView("payroll")}>Payroll</button>
            <button className={`list-group-item list-group-item-action ${view==="attendance" && "active"}`} onClick={() => setView("attendance")}>Attendance</button>
            <button className={`list-group-item list-group-item-action ${view==="slips" && "active"}`} onClick={() => setView("slips")}>Salary Slips</button>
          </div>
        </div>

        <div className="col-md-9">
          {view === "home" && (
            <div className="text-center">
              <h2>Welcome {user?.email || "Admin"} ðŸ‘‹</h2>
              <p>Manage payroll, employees, attendance, and reports in one place.</p>
            </div>
          )}

          {view === "employees" && (
            <div>
              <h3>ðŸ‘¥ Employee Records</h3>
              <table className="table table-striped mt-3">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Designation</th>
                    <th>Basic Salary</th>
                    <th>Allowances</th>
                    <th>Deductions</th>
                    <th>Net Salary</th>
                  </tr>
                </thead>
                <tbody>
                  {employees.map((emp) => (
                    <tr key={emp._id}>
                      <td>{emp.name}</td>
                      <td>{emp.email}</td>
                      <td>{emp.designation}</td>
                      <td>â‚¹{emp.basicSalary}</td>
                      <td>â‚¹{emp.allowances}</td>
                      <td>â‚¹{emp.deductions}</td>
                      <td>â‚¹{calculateNetSalary(emp)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {view === "payroll" && (
            <div>
              <h3>ðŸ’° Payroll Overview</h3>
              <p>Total Payroll: â‚¹{employees.reduce((a, e) => a + calculateNetSalary(e), 0)}</p>
              <p>Highest Salary: â‚¹{Math.max(...employees.map(e => calculateNetSalary(e)))}</p>
              <p>Lowest Salary: â‚¹{Math.min(...employees.map(e => calculateNetSalary(e)))}</p>
              <table className="table table-bordered mt-3">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Net Salary</th>
                  </tr>
                </thead>
                <tbody>
                  {employees.map(emp => (
                    <tr key={emp._id}>
                      <td>{emp.name}</td>
                      <td>â‚¹{calculateNetSalary(emp)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {view === "attendance" && (
            <div>
              <h3>ðŸ“… Attendance Records</h3>
              <table className="table table-striped mt-3">
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Employee</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {attendance.map(att => (
                    <tr key={att._id} className={attendanceColor(att.status)}>
                      <td>{att.date}</td>
                      <td>{att.employee}</td>
                      <td>{att.status}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {view === "slips" && (
            <div>
              <h3>ðŸ“„ Salary Slips</h3>
              <div className="row">
                {employees.map(emp => (
                  <div key={emp._id} className="col-md-4 mb-3">
                    <div className="card p-3 shadow-sm">
                      <h5>{emp.name}</h5>
                      <p>Email: {emp.email}</p>
                      <p>Designation: {emp.designation}</p>
                      <p>Net Salary: â‚¹{calculateNetSalary(emp)}</p>
                      <button className="btn btn-primary" onClick={() => generateSalarySlip(emp)}>
                        Download Slip
                      </button>
                    </div>
                  </div>
                ))}
