import React from "react";
import { FaUserCircle } from "react-icons/fa"; // user icon

export const Navigation = ({ user, onLoginClick, onHomeClick, onFeaturesClick, onAboutClick, onContactClick }) => {
  return (
    <nav id="menu" className="navbar navbar-default navbar-fixed-top">
      <div className="container">
        <div className="navbar-header">
          <a 
            className="navbar-brand page-scroll" 
            onClick={onHomeClick} 
            style={{ cursor: "pointer", display: "flex", alignItems: "center" }}
          >
            <img 
              src="img/logo.png" 
              alt="Smart Payroll Portal" 
              style={{ height: "50px", marginRight: "10px" }} 
            />
            <span style={{ fontSize: "20px", fontWeight: "bold" }}>Payroll Portal</span>
          </a>
        </div>

        <div className="collapse navbar-collapse">
          <ul className="nav navbar-nav navbar-right">
            <li><a onClick={onHomeClick} style={{ cursor: "pointer" }}>Home</a></li>
            <li><a onClick={onFeaturesClick} style={{ cursor: "pointer" }}>Features</a></li>
            <li><a onClick={onAboutClick} style={{ cursor: "pointer" }}>About</a></li>
            <li><a onClick={onContactClick} style={{ cursor: "pointer" }}>Contact</a></li>

            <li>
              {user ? (
                <div style={{ display: "flex", alignItems: "center", marginLeft: "15px" }}>
                  <FaUserCircle size={28} style={{ marginRight: "8px", color: "#007bff" }} />
                  <span style={{ fontWeight: "bold" }}>{user.email}</span>
                </div>
              ) : (
                <button 
                  onClick={onLoginClick}
                  className="btn btn-primary"
                  style={{
                    marginLeft: "10px",
                    padding: "8px 20px",
                    marginTop: "10px",
                    borderRadius: "25px",
                    backgroundColor: "#007bff",
                  }}
                >
                  Login
                </button>
              )}
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
};
