import React, { useState, useEffect } from "react";
import { jsPDF } from "jspdf";
import axios from "axios"; // for API calls
import "./dashboard.css";

export const Dashboard = ({ user }) => {
  const [view, setView] = useState("home"); // current section
  const [employees, setEmployees] = useState([]);
  const [attendance, setAttendance] = useState([]);

  // Fetch employees from backend
  useEffect(() => {
    const fetchData = async () => {
      try {
        const empRes = await axios.get("http://localhost:5000/api/employees");
        const attRes = await axios.get("http://localhost:5000/api/attendance");
        setEmployees(empRes.data);
        setAttendance(attRes.data);
      } catch (err) {
        console.error("Error fetching data:", err);
      }
    };
    fetchData();
  }, []);

  // Salary calculation
  const calculateNetSalary = (emp) =>
    emp.basicSalary + emp.allowances - emp.deductions;

  // Generate Salary Slip
  const generateSalarySlip = (emp) => {
    const doc = new jsPDF();
    doc.setFontSize(18);
    doc.text("Salary Slip", 80, 20);

    doc.setFontSize(12);
    doc.text(`Name: ${emp.name}`, 20, 40);
    doc.text(`Email: ${emp.email}`, 20, 50);
    doc.text(`Designation: ${emp.designation}`, 20, 60);
    doc.text(`Basic Salary: ‚Çπ${emp.basicSalary}`, 20, 80);
    doc.text(`Allowances: ‚Çπ${emp.allowances}`, 20, 90);
    doc.text(`Deductions: ‚Çπ${emp.deductions}`, 20, 100);
    doc.text(`Net Salary: ‚Çπ${calculateNetSalary(emp)}`, 20, 120);

    doc.save(`${emp.name}_SalarySlip.pdf`);
  };

  return (
    <div id="dashboard" className="dashboard-container">
      <div className="container">
        <div className="section-title text-center">
          <h2>Welcome {user?.email || "Admin"} üëã</h2>
          <p>Manage payroll, employees, attendance, and reports in one place.</p>
        </div>

        {/* ---------------- Home Navigation ---------------- */}
        {view === "home" && (
          <div className="row dashboard-grid">
            <div className="col-md-4 col-sm-6">
              <div className="dashboard-card" onClick={() => setView("employees")}>
                <i className="fa fa-users fa-3x"></i>
                <h3>Employee Management</h3>
                <p>Add, update, or remove employee records.</p>
              </div>
            </div>

            <div className="col-md-4 col-sm-6">
              <div className="dashboard-card" onClick={() => setView("payroll")}>
                <i className="fa fa-money fa-3x"></i>
                <h3>Payroll Processing</h3>
                <p>Calculate salaries with tax, bonuses, and overtime.</p>
              </div>
            </div>

            <div className="col-md-4 col-sm-6">
              <div className="dashboard-card" onClick={() => setView("attendance")}>
                <i className="fa fa-calendar-check-o fa-3x"></i>
                <h3>Attendance & Leave</h3>
                <p>Track attendance and approve leave requests.</p>
              </div>
            </div>

            <div className="col-md-4 col-sm-6">
              <div className="dashboard-card" onClick={() => setView("slips")}>
                <i className="fa fa-file-text fa-3x"></i>
                <h3>Salary Slips</h3>
                <p>Generate and download employee salary slips.</p>
              </div>
            </div>
          </div>
        )}

        {/* ---------------- Employees Section ---------------- */}
        {view === "employees" && (
          <div className="section-content">
            <div className="d-flex justify-content-between align-items-center">
              <h3>üë• Employee Records</h3>
              <button className="btn btn-secondary" onClick={() => setView("home")}>
                ‚Üê Back
              </button>
            </div>
            <table className="table mt-3">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Designation</th>
                  <th>Basic Salary</th>
                </tr>
              </thead>
              <tbody>
                {employees.map((emp) => (
                  <tr key={emp._id}>
                    <td>{emp.name}</td>
                    <td>{emp.email}</td>
                    <td>{emp.designation}</td>
                    <td>‚Çπ{emp.basicSalary}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* ---------------- Payroll Section ---------------- */}
        {view === "payroll" && (
          <div className="section-content">
            <div className="d-flex justify-content-between align-items-center">
              <h3>üí∞ Payroll Processing</h3>
              <button className="btn btn-secondary" onClick={() => setView("home")}>
                ‚Üê Back
              </button>
            </div>
            <table className="table mt-3">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Basic</th>
                  <th>Allowances</th>
                  <th>Deductions</th>
                  <th>Net Salary</th>
                </tr>
              </thead>
              <tbody>
                {employees.map((emp) => (
                  <tr key={emp._id}>
                    <td>{emp.name}</td>
                    <td>‚Çπ{emp.basicSalary}</td>
                    <td>‚Çπ{emp.allowances}</td>
                    <td>‚Çπ{emp.deductions}</td>
                    <td>‚Çπ{calculateNetSalary(emp)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* ---------------- Attendance Section ---------------- */}
        {view === "attendance" && (
          <div className="section-content">
            <div className="d-flex justify-content-between align-items-center">
              <h3>üìÖ Attendance Records</h3>
              <button className="btn btn-secondary" onClick={() => setView("home")}>
                ‚Üê Back
              </button>
            </div>
            <table className="table mt-3">
              <thead>
                <tr>
                  <th>Date</th>
                  <th>Employee</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {attendance.map((att) => (
                  <tr key={att._id}>
                    <td>{att.date}</td>
                    <td>{att.employee}</td>
                    <td>{att.status}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {/* ---------------- Salary Slips Section ---------------- */}
        {view === "slips" && (
          <div className="section-content">
            <div className="d-flex justify-content-between align-items-center">
              <h3>üìÑ Salary Slips</h3>
              <button className="btn btn-secondary" onClick={() => setView("home")}>
                ‚Üê Back
              </button>
            </div>
            <div className="row mt-3">
              {employees.map((emp) => (
                <div key={emp._id} className="slip-card col-md-4">
                  <h4>{emp.name}</h4>
                  <p>Email: {emp.email}</p>
                  <p>Designation: {emp.designation}</p>
                  <p>Net Salary: ‚Çπ{calculateNetSalary(emp)}</p>
                  <button
                    className="btn btn-primary"
                    onClick={() => generateSalarySlip(emp)}
                  >
                    Download Slip
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};
